mapper.update:
UPDATE board
SET subject = #{subject}, content = #{content}

	, fileName = #{fileName}

WHERE articleNo = #{articleNo}

mapper.select:

SELECT NVL(MAX(re_step), 0)
FROM board
WHERE ref = #{ref}
AND re_step > #{re_step}
AND re_level <= #{re_level}

mapper.update:

UPDATE board
SET re_step = re_step + 1
WHERE ref = #{ref}
AND re_step > #{re_step}
